mapper.select:
SELECT ROW_NUMBER() OVER() AS RNUM, B.BOARD_NUM, C.BOARD_CGY_TXT, B.USER_ID, B.BOARD_TITLE, B.BOARD_TXT, B.BOARD_TAG, B.COMMENT_YN, B.BOARD_VIEWCNT, DATE_FORMAT(B.BOARD_DATE,'%Y-%m-%d') AS BOARD_DATE, (SELECT COUNT(*) FROM TB_BOARD_FILE TBF WHERE TBF.BOARD_NUM = B.BOARD_NUM) FROM BOARD B LEFT JOIN CATEGORY C ON B.BOARD_CGY_NUM = C.BOARD_CGY_NUM
WHERE B.BOARD_NUM = #{bnum}
ORDER BY BOARD_NUM;

mapper.update:
	UPDATE BOARD
	SET	BOARD_CGY_NUM = #{board_cgy_num},
      BOARD_TITLE = #{board_title},
      BOARD_TXT = #{board_txt},
      BOARD_TAG = #{board_tag}
	WHERE BOARD_NUM = #{board_num}

mapper.insert:
INSERT INTO
	TB_BOARD_FILE(
		BOARD_NUM,
		FILE_ID
	)
	VALUE(
		#{bnum},
		#{fileId}
	)
